import React, {useState, useCallback} from 'react';
import {
  MDBEdgeHeader,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBJumbotron,
  MDBIcon,
  MDBAnimation,
  MDBBtn
} from 'mdbreact';
import LightboxGallery from '../components/Gallery.js'

import vivi1 from '../assets/vivi-1.jpg';
import vivi2 from '../assets/vivi-2.jpg';
import vivi3 from '../assets/vivi-3.jpg';

const photos = [
  {
    src: vivi1,
	width:3,
	height:4
  },
  {
    src: vivi2,
	width:3,
	height:4
  },
  {
    src: vivi3,
	width:4,
	height:3
  }
];


function Vivi() {
	const openLightbox = useCallback((event, { photo, index }) => {
		setCurrentImage(index);
		setViewerIsOpen(true);
	  }, []);

	  const closeLightbox = () => {
		setCurrentImage(0);
		setViewerIsOpen(false);
	  };

	const [currentImage, setCurrentImage] = useState(0);
	const [viewerIsOpen, setViewerIsOpen] = useState(false);

  return (
    <>
    <MDBEdgeHeader color='indigo' className='sectionPage' />
      <MDBAnimation type='zoomIn' duration='500ms'>
      <MDBContainer>
	  	<h1>Vivi Coming Soon!</h1>
      </MDBContainer>
      </MDBAnimation>
    </>
  );
};

export default Vivi;
